              <script>
			var menu = document.getElementsByClassName('menu')[0];
        var per = 0;
        var menu_div;
        var newLogo = document.getElementById("logoNew");
        var overlay = document.getElementsByClassName("overlay")[0];
        var menu_button = document.getElementById("menu-button");
        
        function menuExpand(){
            if(per>=75)
                return;
        
            menu_div = document.getElementById("menu-div");
            
            var id = setInterval(frame, 15);
            menu.style.animation = "expand 500ms 500ms ease-out forwards";
            overlay.style.animation = "overlay-anim 700ms linear";
            function frame() {
                if (per >= 75) {
                clearInterval(id);
                
                } else {
                per+=5; 
                
                menu_div.style.opacity = (per-30)/30;
                
                }
            }
            menu_button.style.opacity = 0;
            $('.ml14 .letters').each(function(){
            $(this).html($(this).text().replace(/([^\x00-\x80]|\w)/g, "<span class='letter'>$&</span>"));
            });
            anime.timeline({loop: false})
            .add({
                targets: '.ml14 .line',
                scaleX: [0,1],
                opacity: [0.5,1],
                easing: "easeInOutExpo",
                duration: 900,
                delay: 1000
            }).add({
                targets: '.ml14 .letter',
                opacity: [0,1],
                translateX: [40,0],
                translateZ: 0,
                scaleX: [0.3, 1],
                easing: "easeOutExpo",
                duration: 800,
                offset: '-=600',
                delay: function(el, i) {
                return 150 + 25 * i;
                }
            });
        }
        $(document).click(function(e){
						console.log(e.target);
            if ( per==75) {
                console.log("Menu Hide");
                menuHide();
            }
        })
        function menuHide(){
            overlay.style.animation = "";
            menu.style.animation = "contract 200ms ease-out forwards";
            var id = setInterval(frame, 1);
            function frame() {
                if (per == 0) {
                    menu_div.style.opacity = 0;
                    menu_button.style.opacity = 1;
                    clearInterval(id);
                
                } else {
                per-=5;  
                
                }
            }
        }
       
		</script>